import React from 'react';
import styled from 'styled-components';
import Logo from '../header/Logo.js';

import {
  Switch,
  Route,
  Link,
} from 'react-router-dom';

const ThumbnailS = styled.div`
  width: 40%;
  height: 27vh;
  text-align: center;
  background: pink;
  display: flex;
  flex-wrap: wrap;
`;

const ImageBoxS = styled.div`
  width: 100%;
  height: 75%;
  text-align: center;
  border: 1px solid;
  background: red;
`;

const TextBoxS = styled.div`
  width: 100%;
  height: 25%;
  text-align: center;
  border-bottom: 1px solid;
  border-left: 1px solid;
  border-right: 1px solid;
  background: red;
  display: flex;
`;

function Thumbnail({ children }) {

  return <ThumbnailS>
      <ImageBoxS>썸네일 이미지</ImageBoxS>
      <TextBoxS>
        <div width='100%'>Title</div>
        <div width='100%'>Author</div>
        <div width='50%'>Price</div>
        <div width='50%'>Like</div>
      </TextBoxS>
    </ThumbnailS>

}

export default Thumbnail;
