import React from 'react';
import styled from 'styled-components';
import Logo from '../header/Logo.js';

import {
  Switch,
  Route,
  Link,
} from 'react-router-dom';

const BackgroundS = styled.div`
 min-width: 1920px;
 min-height: 1080px;
 background:gray;
`;

const LoginPageS = styled.div`
  position: fixed;
  display: flex;
  flex-wrap: wrap;

  width: 30%;
  height: 40%;
  margin-top: 15%;
  margin-left: 35%;
  display: flex;
  justify-content: center;
`;

const UnderLineS = styled.div`
  border-bottom: dashed;
  width: 75%;
  height: 0px;
  margin-top: ${(props) => props.margintop};
`;

const BlockS = styled.input`
  width: 55%;
  height: 10%;
  border: 2px solid #bbb;
  border-radius: 6px;
  font-size: 16px;
  text-align: center;
  margin-top: ${(props) => props.margintop};
  margin-left: ${(props) => props.marginleft};
`;

const ButtonS = styled.button`
  width: 20%;
  height: 24.4%;
  border: 2px solid #bbb;
  border-radius: 6px;
  font-size: 16px;
  text-align: center;
  margin-top: ${(props) => props.margintop};
  margin-left: ${(props) => props.marginleft};
`;

const BlankS=styled.div`
  width: ${(props) => props.width};
  height: ${(props) => props.height};
`;

const NavButtonS = styled.div`
  cursor: pointer;
  text-align : center;
  width: 10%;
  height: 5%;
  font-size: 16px;
  text-align: center;
  font-family:'bitbit';
  font-weight: 1;
  margin-left: ${(props) => props.marginleft};
  margin-right: ${(props) => props.marginright};
  margin-top: ${(props) => props.margintop};
  color: ${(props) => props.color};
`;

function Loginpage({ children }) {

  return <BackgroundS>
    <LoginPageS>
      <Logo>60px</Logo>
      <UnderLineS margintop='-3%'></UnderLineS>
      <BlockS placeholder='ID' margintop='-6%' marginleft='-21%'></BlockS>
      <BlankS width='100%' height='0px'></BlankS>
      <BlockS placeholder='Password' margintop='-16%' marginleft=''></BlockS>
      <ButtonS margintop='-24.5%' marginleft='1%'>로그인</ButtonS>
      <BlankS width='100%' height='0px'></BlankS>
      <NavButtonS marginleft='12%' margintop='-22%'>ID찾기</NavButtonS>
      <NavButtonS marginleft='' margintop='-22%'>PW찾기</NavButtonS>
      <NavButtonS marginleft='auto' marginright='12%' margintop='-22%'>회원가입</NavButtonS>
    </LoginPageS>
  </BackgroundS>

}

export default Loginpage;
