import React from 'react';
import styled from 'styled-components';
import Logo from '../header/Logo.js';

import {
  Switch,
  Route,
  Link,
} from 'react-router-dom';

const ThumbnailS = styled.div`
  width: 40%;
  height: 27vh;
  text-align: center;
  display: flex;
  flex-wrap: wrap;
  line-height: 1000%;
`;

const ImageBoxS = styled.div`
  width: 100%;
  height: 75%;
  text-align: center;
  border: 1px solid;
  background: pink;
`;

const TextBoxS = styled.div`
  width: 100%;
  height: 25%;
  text-align: center;
  border-bottom: 1px solid;
  border-left: 1px solid;
  border-right: 1px solid;
  display: flex;
  flex-wrap: wrap;
`;

const BlankS=styled.div`
  width: ${(props) => props.width};
  height: ${(props) => props.height};
`;

function Thumbnail({ children }) {

  return <ThumbnailS>
      <ImageBoxS>썸네일 이미지</ImageBoxS>
      <TextBoxS>
        <div>Title</div>
        <BlankS width='100%'></BlankS>
        <div>Author</div>
        <BlankS width='100%'></BlankS>
        <div>Price</div>
        <BlankS width='50%'></BlankS>
        <div>Like</div>
      </TextBoxS>
    </ThumbnailS>

}

export default Thumbnail;
