import React from 'react';
import styled from 'styled-components';
import Logo from '../header/Logo.js';

import {
  Switch,
  Route,
  Link,
} from 'react-router-dom';

const LoginPageS = styled.div`
  position: fixed;
  display: flex;
  flex-wrap: wrap;

  width: 30%;
  height: 40%;
  margin-top: 15%;
  margin-left: 35%;
  display: flex;
  justify-content: center;
  background: plum;
`;

const UnderLineS = styled.div`
  border-bottom: dashed;
  width: 75%;
  height: 0px;
  margin-top: ${(props) => props.margintop};
`;

const BlockS = styled.input`
  width: 50%;
  height: 10%;
  border: 2px solid #bbb;
  border-radius: 6px;
  font-size: 16px;
  text-align: center;
  margin-top: ${(props) => props.margintop};
  margin-left: ${(props) => props.marginleft};
`;

const ButtonS = styled.button`
  width: 20%;
  height: 25%;
  border: 2px solid #bbb;
  border-radius: 6px;
  font-size: 16px;
  text-align: center;
  margin-top: ${(props) => props.margintop};
  margin-left: ${(props) => props.marginleft};
`;

const NavButtonS = styled.div`
  cursor: pointer;
  text-align : center;
  width: 10%;
  height: 5%;
  font-size: 16px;
  text-align: center;
  font-family:'bitbit';
  font-weight: 1;
  margin-left: ${(props) => props.marginleft};
  margin-top: ${(props) => props.margintop};
  color: ${(props) => props.color};
  background: white;
`;

function Loginpage({ children }) {

  return <LoginPageS>
      <Logo>60px</Logo>
      <UnderLineS margintop='-7%'></UnderLineS>
      <BlockS placeholder='ID' margintop='-15%'></BlockS>
      <ButtonS margintop='-15%' marginleft='1%'>로그인</ButtonS>
      <BlockS placeholder='Password' margintop='-19.2%' marginleft='-21%'></BlockS>
      <NavButtonS marginleft='' margintop='-23%'>ID찾기</NavButtonS>
      <NavButtonS marginleft='-42%' margintop='-23%'>PW찾기</NavButtonS>
      <NavButtonS marginleft='-9%' margintop='-23%'>회원가입</NavButtonS>
    </LoginPageS>
}

export default Loginpage;
