import React from 'react';
import styled from 'styled-components';
import Logo from '../header/Logo.js';

import {
  Switch,
  Route,
  Link,
} from 'react-router-dom';

const ThumbnailS = styled.div`
  width: 40%;
  height: 100px;
  text-align: center;
  background: pink;
  display: flex;
`;

const ImageBoxS = styled.div`
  width: 100%;
  height: 75%;
  text-align: center;
  border: 1px solid;
  background: red;
`;

const TextBoxS = styled.div`
  width: 100%;
  height: 25%;
  text-align: center;
  border-bottom: 1px solid;
  border-left: 1px solid;
  border-right: 1px solid;
  background: red;
`;

const TitleS = styled.div`
  display: flex;
  font-family: 'bitbit';
  vertical-align: bottom;
  align-items:center;
  width : ${(props) => props.width};
  height: 36px;
  cursor: pointer;
  margin-left: ${(props) => props.marginleft};
  text-align: center;
  font-weight: 1;
  font-size: 300%;
  color: ${(props) => props.color};
`;

const UnderLineS = styled.div`
  border-bottom: dashed;
  width: 75%;
  height: 0px;
  margin-top: ${(props) => props.margintop};
`;

const BlockS = styled.input`
  padding: 1% 0px;
  width: 60%;
  border: 2px solid #bbb;
  border-radius: 6px;
  font-size: 2vh;
  text-align: center;
  margin-top: ${(props) => props.margintop};
  margin-left: ${(props) => props.marginleft};
`;

const ButtonS = styled.button`
  padding: 2% 0px;
  cursor: pointer;
  width: 67.5%;
  border: 2px solid #bbb;
  border-radius: 6px;
  font-size: 16px;
  text-align: center;
  margin-top: ${(props) => props.margintop};
  margin-left: ${(props) => props.marginleft};
`;

const BlankS=styled.div`
  width: ${(props) => props.width};
  height: ${(props) => props.height};
`;

const NavButtonS = styled.div`
  cursor: pointer;
  text-align : center;
  width: 10%;
  height: 5%;
  font-size: 16px;
  text-align: center;
  font-family:'bitbit';
  font-weight: 1;
  margin-left: ${(props) => props.marginleft};
  margin-right: ${(props) => props.marginright};
  margin-top: ${(props) => props.margintop};
  color: ${(props) => props.color};
`;

const IconBoxS = styled.img`
  width: 6.5%;
  height: 6.5%;
  margin-left: ${(props) => props.marginleft};
  margin-right: ${(props) => props.marginright};
  margin-top: ${(props) => props.margintop};
`;

function Thumbnail({ children }) {

  return <ThumbnailS>
      <ImageBoxS>썸네일 이미지</ImageBoxS>
      <TextBoxS>
        <div>Title</div>
        <div>Author</div>
        <div>Price</div>
        <div>Like</div>
      </TextBoxS>
    </ThumbnailS>

}

export default Thumbnail;
